<?phprequire '../vendor/autoload.php';use Symfony\Component\Routing\Matcher\UrlMatcher;use Symfony\Component\Config\FileLocator;use Symfony\Component\Routing\Loader\YamlFileLoader;use Symfony\Component\Routing\RequestContext;use Symfony\Component\HttpFoundation\Request;$request = Request::createFromGlobals();$fileLocator = new FileLocator([__DIR__ . '/../config']);$loader = new YamlFileLoader($fileLocator);$routes = $loader->load('routing.yaml');$requestContext = new RequestContext();$requestContext->fromRequest($request);$matcher = new UrlMatcher($routes, $requestContext);$parameters = $matcher->match($request->getPathInfo());$controllerName = explode('::', $parameters['_controller'])[0];$actionName = explode('::', $parameters['_controller'])[1];$controller = new $controllerName();unset($parameters['_controller']);unset($parameters['_route']);echo call_user_func_array([$controller, $actionName], $parameters);